import math

pi = math.pi
log = math.log10
ln = math.log
sqrt = math.sqrt
sinh = math.sinh
cosh = math.cosh
tanh = math.tanh
power = math.pow


def sin(num):
    return math.sin(math.radians(num))


def asin(num):
    return math.degrees(math.atan(num))


def cos(num):
    return math.cos(math.radians(num))


def acos(num):
    return math.degrees(math.acos(num))


def tan(num):
    return math.tan(math.radians(num))


def atan(num):
    return math.degrees(math.atan(num))
